

var hand = 'left';

$("#shoterLeft").click(function() {
  if (hand == 'left') {
    $("#ball").animate({opacity: 0.60}, 50);
    $("#ball").animate({
      opacity: 0.9,
      left: "+=100%",
    }, 350, function() {
    });
    $("#ball").animate({opacity: 1}, 30);
    hand = 'right';
    console.log(hand+' turn');
  }

});


$("#shoterRight").click(function() {
  if (hand == 'right') {
      $("#ball").animate({opacity: 0.60}, 50);
      $("#ball").animate({
        opacity: 0.9,
        left: "-=100%",
      }, 350, function() {

      });
      $("#ball").animate({opacity: 1}, 30);
      hand = 'left';
      console.log(hand+' turn');
  }
});


$("#homeRight").click(function() {
  $("#shoterRight").animate({opacity: 0.6}, 10);
  var callPoint = event.pageY;
  var playgroundTop = $("#playground").offset().top;
  var playgroundMax = $("#playground").height() - 78;
  var move = callPoint - playgroundTop-38;
  move = (move > playgroundMax) ? (playgroundMax) : move;
  move = (move < 0) ? (move + 38) : move;
  console.log('Right Shoter:' + callPoint + '-' + playgroundTop + '=' + move);
  $("#shoterRight").animate({top: move}, 130);
  $("#shoterRight").animate({opacity: 1}, 10);
});
